  }
  .autor {
      flex-flow: row-reverse;
      text-align: right;
  }

    h3{
      font-size: 1rem
  }

  .container{
    display: flex;
    margin-bottom: 0.2rem
}


.medo{
background: linear-gradient(90deg, #000000 15%, #ddd9d5 10%);
border: solid 2px #000000;
font-size: 0.9rem;
}
.paralisia{
background: linear-gradient(90deg, #435178 15%, #ddd9d5 10%);
