</div>
<script>
    function toChat(condicao = 'Abalado'){
        game.tormenta20.macros.msgFromJournal(condicao, "tormenta20.condicoes");
    }
</script>
    `,
    buttons: {
        no: {
            icon: "<i class='fas fa-times'></i>",
            label: `Fechar`,
            callback: () => close
        },
    },
}).render(true);
