command:
// Usável para adicionar múltiplas iniciativas a chefões!

const combat = game.combat;
if(token && combat) {
  let create = {
    tokenId: token.id,
    sceneId: token.scene.id,
    actorId: token.actor.id,
    hidden: token.document.hidden
  };
  await combat.createEmbeddedDocuments("Combatant", [create]);
}
